mapper.update:
UPDATE flipnet.flipnet_serial
SET status = #{status}, gmt_modified = NOW(), response_message = #{responseMessage},
external_serial_no = #{externalSerialNo}

    ,request_message = #{requestMessage}

WHERE id::text = #{id}

UPDATE flipnet.flipnet_serial
SET status = #{status}, gmt_modified = NOW(), receipt = #{receipt},
time_served = #{timeServed}
WHERE id::text = #{id}

mapper.select:
SELECT * FROM flipnet.flipnet_serial

    WHERE id = #{id} AND merchant_id = #{merchantId}


    WHERE id = #{id}


    WHERE merchant_id = #{merchantId}
